(()=>{"use strict";(t=>{let e;const n=document.getElementById("timer-hours"),o=document.getElementById("timer-minutes"),r=document.getElementById("timer-seconds");console.dir(n);e=setInterval((()=>{(()=>{let t=(()=>{let t=(new Date("30 november 2023").getTime()-(new Date).getTime())/1e3;return Math.floor(t/60/60/24),{timerRemaining:t,hours:Math.floor(t/60/60%24).toString().padStart(2,"0"),minutes:Math.floor(t/60%60).toString().padStart(2,"0"),seconds:Math.floor(t%60).toString().padStart(2,"0")}})();n.textContent=t.hours,o.textContent=t.minutes,r.textContent=t.seconds,console.log("я вывожусь раз в секунду"),t.timerRemaining<=0&&(n.textContent="00",o.textContent="00",r.textContent="00",clearInterval(e))})()}),1e3)})()})();